package com.quantum_guys.dncc_eco_sync.models;public class Comment {    private String id, username, image, post_id, comment, timestamp, commentId;    public Comment() {    }    public Comment(String id, String username, String image, String post_id, String comment, String timestamp, String commentId) {        this.id = id;        this.username = username;        this.image = image;        this.post_id = post_id;        this.comment = comment;        this.timestamp = timestamp;        this.commentId = commentId;    }    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }    public String getImage() {        return image;    }    public void setImage(String image) {        this.image = image;    }    public String getPost_id() {        return post_id;    }    public void setPost_id(String post_id) {        this.post_id = post_id;    }    public String getComment() {        return comment;    }    public void setComment(String comment) {        this.comment = comment;    }    public String getTimestamp() {        return timestamp;    }    public void setTimestamp(String timestamp) {        this.timestamp = timestamp;    }    public String getCommentId() {        return commentId;    }    public void setCommentId(String commentId) {        this.commentId = commentId;    }}