package com.quantum_guys.dncc_eco_sync.retrofit;import java.util.concurrent.TimeUnit;import okhttp3.OkHttpClient;import retrofit2.Retrofit;import retrofit2.converter.gson.GsonConverterFactory;public class RetrofitClient {    private static Retrofit retrofit = null;    public static Retrofit getClient(String baseUrl) {        OkHttpClient.Builder client = new OkHttpClient.Builder();        client.connectTimeout(100, TimeUnit.SECONDS);        client.readTimeout(100, TimeUnit.SECONDS);        client.writeTimeout(100, TimeUnit.SECONDS);        if (retrofit == null) {            retrofit = new Retrofit.Builder()                    .baseUrl(baseUrl)                    .addConverterFactory(GsonConverterFactory.create())                    .client(client.build())                    .build();        }        return retrofit;    }}