package com.quantum_guys.dncc_eco_sync.viewmodel;import android.app.Application;import androidx.annotation.NonNull;import androidx.lifecycle.AndroidViewModel;import androidx.lifecycle.LiveData;import com.quantum_guys.dncc_eco_sync.repository.QuizResultRepository;import com.quantum_guys.dncc_eco_sync.ui.activities.quiz.BattleModel;import java.util.List;public class BattleViewModel extends AndroidViewModel {    public final LiveData<List<BattleModel>> battles;    final QuizResultRepository repository;    public BattleViewModel(@NonNull Application application) {        super(application);        repository = new QuizResultRepository(application);        battles = repository.getAllNotes();    }    public void insert(BattleModel result) {        repository.insertBattle(result);    }    public LiveData<BattleModel> get(String id) {        return repository.getBattle(id);    }    public BattleModel getBattle(String id) {        return repository.getBattleOffline(id);    }    public void delete(String result) {        repository.deleteBattle(result);    }}